function getAddressItems(){
  fetch(jsonUrl + '?'+ new URLSearchParams({
    $where: "address = '"+data.selectProjectAddress+"' and parcel_number IS NOT NULL",
    $limit: 100,
    $order: 'address ASC, lot ASC',
    $select: "address,address_number,address_number_suffix,street_name,street_type,unit_number,block,lot,zip_code"
}))
  .then(response => response.json())
  .then(respData => {
    if(respData && respData.status == "success" && respData.data && respData.data.items && respData.data.items.length > 0){
        // response ok
        dataItems = respData.data.items;
        processAddressItems(dataItems);

        instance.triggerChange();

    }else{
      // NOT_FOUND
    }

  }).catch((error) => {
   console.log(error);
  }).finally(() => {
    // finally
  });
}

function processAddressItems(addressItems){
  window.formio_sfds_project_address_lookup.setValue('projectAddressDataItems', JSON.stringify(addressItems));
  // processAddressItems
  window.formio_sfds_project_address_lookup.updateAddressFields(addressItems[0]);
  window.formio_sfds_project_address_lookup.setValue('lookupBlockNumber', addressItems[0]['block']);
  window.formio_sfds_project_address_lookup.setValue('lookupLotNumber', addressItems[0]['lot']);
