function initData(){
  // address init data
  if(data.selectProjectAddress != data.projectAddress){
    window.formio_sfds_project_address_lookup.resetAddressFields();
    if(data.selectProjectAddress){
      getAddressItems();
    }else{
      instance.triggerChange();
    }
  }
}

