function getBlkLotItems(){
  var jsonUrl = 'https://address-microservice-js.vercel.app/api/eas/json';
  fetch(jsonUrl + '?'+ new URLSearchParams({
    $where: "block = '"+data.blockNumber+"' and lot = '"+data.lotNumber+"' and parcel_number IS NOT NULL",
    $limit: 100,
    $order: 'street_name ASC, address_number ASC, address ASC',
    $select: "address,address_number,address_number_suffix,street_name,street_type,unit_number,block,lot,zip_code"
}))
  .then(response => response.json())
  .then(respData => {
    if(respData && respData.status == "success" && respData.data && respData.data.items && respData.data.items.length > 0){
        // response ok
        dataItems = respData.data.items;
        if(dataItems.length){
          processBlkLotItems(dataItems);
        }else{
          showMessage('error');
        }
    }else{
      showMessage('error');
    }

  }).catch((error) => {
   console.log(error);
  }).finally(() => {
    // finally
  });
}

function processBlkLotItems(addressItems){
  // processBlkLotItems
  window.formio_sfds_project_address_lookup.setValue('projectAddressDataItems', JSON.stringify(addressItems));
  if(addressItems.length > 1){
    showMessage('multiple');
  }else{
    showMessage('single');
    data.lookupAddress = addressItems[0]['address'];
    window.formio_sfds_project_address_lookup.setValue('lookupAddress', addressItems[0]['address']);
  }
  window.formio_sfds_project_address_lookup.updateAddressFields(addressItems[0]);
}

function showMessage(msg){
  // possible msg: single, multiple, error
  var lookupAddressMessage = utils.getComponent(form.components, 'lookupAddressMessage');
  lookupAddressMessage.setValue(msg);
  lookupAddressMessage.triggerChange();
}

