function setProjectAddress(){
    if(data.chooseAddress && data.chooseAddress != data.projectAddress){
      dataItems = JSON.parse(data.projectAddressDataItems);
      dataItems.forEach((item, index) => {
        if(data.chooseAddress == item["address"]){
          window.formio_sfds_project_address_lookup.updateAddressFields(item);
        }
      })
    }
}

(function(_w, _d, undefined){
  _w.formio_sfds_project_address_lookup = {
    updateAddressFields: function(addressItem){
      var map = {
        'address': 'projectAddress',
        'address_number': 'projectAddressNumber',
        'address_number_suffix': 'projectAddressNumberSuffix',
        'street_name': 'projectAddressStreetName',
        'street_type': 'projectAddressStreetType',
        'unit_number': 'projectAddressUnitNumber',
        'block': 'projectAddressBlock',
        'lot': 'projectAddressLot',
        'zip_code': 'projectAddressZip'
      }
      for(var prop in map){
        var value = '';
        if(addressItem.hasOwnProperty(prop)) value = addressItem[prop];
        this.setValue(map[prop], value);
      }
    },
    resetAddressFields: function(){
      data["projectAddressDataItems"] = ""
      var map = {
        'address': 'projectAddress',
        'address_number': 'projectAddressNumber',
        'address_number_suffix': 'projectAddressNumberSuffix',
        'street_name': 'projectAddressStreetName',
        'street_type': 'projectAddressStreetType',
        'unit_number': 'projectAddressUnitNumber',
        'block': 'projectAddressBlock',
        'lot': 'projectAddressLot',
        'zip_code': 'projectAddressZip'
      }
      for(var prop in map){
        var value = '';
        this.setValue(map[prop], value);
      }
    },
    setValue: function(key, value){
      var comp = utils.getComponent(form.components, key);
      var el = document.querySelector('#'+comp.id+' input');
      if(el){
        el.value = value;
        data[key] = value;
      }else{
        console.log('Cannot setValue of component '+key);
      }
    }
  }
})(window, document);

